import React, {useState} from 'react';
import {Link} from 'react-router-dom'

function Form (props) {
    const {submitForm, initialName, initialPetType, initialDescription, initialSkills} = props
    const [name, setName] = useState(initialName);
    const [petType, setPetType] = useState(initialPetType);
    const [description, setDescription] = useState(initialDescription);
    const [skills, setSkills] = useState(initialSkills);
    


    return(
        <div>
            <form onSubmit={(e) => submitForm(e, {name, petType, description, skills})}>
                <div className=''>
                    <div className=''>
                        <label htmlFor='name'>Name</label>
                        <input type="text" className="" name='name' value={name} onChange={(e) =>{setName(e.target.value)}}/>
                    </div>
                    <div className=''>
                        <label htmlFor='petType'>Pet Type</label>
                        <input type="text" className="" name='petType' value={petType} onChange={(e) =>{setPetType(e.target.value)}}/>
                    </div>
                    <div className=''>
                        <label htmlFor='description'>Description</label>
                        <input type="text" className="" name='description' value={description} onChange={(e) =>{setDescription(e.target.value)}}/>
                    </div>
                    <div className=''>
                        <label htmlFor='skills'>Skills</label>
                        <select onChange={(e) => setS(e.target.value)}>
                            <option></option>
                        </select>
                        {/* <input type="text" className="" name='skills' value={skills} onChange={(e) =>{setSkills(e.target.value.split(','))}}/> */}
                    </div>
                    <Link to={"/"} className="">Cancel</Link>
                    <button className=''>Submit</button>
                </div>
            </form>
        </div>
    );
}

export default Form;
