import React, {useState} from "react";
import {useNavigate, Link} from 'react-router-dom';
import axios from 'axios';
import Format from "../reusables/Format";


function NewPet () {
    const [errors, setErrors] = useState({})
    const [inDb, setInDb] = useState({})
    const [erro, setErro] = useState('')

    const navigate = useNavigate()
    const submitForm = (e, data) =>{
        e.preventDefault();

        axios.get("http://localhost:8000/api/pets")
        .then((res) => {
            setInDb(res.data.pets)
            console.log(inDb, 'in db')
            console.log(data, 'in db')
            
            for(var i=0; i<inDb.length; i++ ){
                if(data.name == inDb.name){
                    console.log("already exists")
                    return false
                }
            }
        })
        .catch((err) => console.log("something went wrong in API request", err))

    
        
        // axios.post("http://localhost:8000/api/pets", data)
        // .then((res) => {
        //     console.log(res);
        //     navigate("/")
        // })
        // .catch((err) => {
        //     console.log("something went wrong with API request", err);
        //     // console.log(err.reponse.data.err.errors)
        //     setErrors(err.response.data.error.errors)
        // })
    }

    return(
        <div>
            <div className=''>
                <div className='container-fluid'>
                    <h1 className='navbar-brand'>Pet Shelter</h1>
                    <Link to={"/"} className="btn btn-primary btn-sm">Home</Link>
                </div>
            </div>
            <div>
                {errors.name && <p className="">{errors.name.message}</p>}
                {errors.petType && <p className="">{errors.petType.message}</p>}
                {errors.description && <p className="">{errors.description.message}</p>}
                {errors.skills && <p className="">{errors.skills.message}</p>}
                <Format  
                submitForm = {submitForm}
                initialName = ''
                initialPeType = ''
                initialDescription = ''
                initialSkills = ''
                />
            </div>
        </div>
    );
}

export default NewPet;
