import React, {useState} from "react";
import {useNavigate, Link} from 'react-router-dom';
import axios from 'axios';
import Form from "../reusables/Form";


function NewPet () {
    const [errors, setErrors] = useState({})
    const [inDb, setInDb] = useState({})
    c

    const navigate = useNavigate()
    const submitForm = (e, data) =>{
        e.preventDefault();

        axios.get("http://localhost:8000/api/pets")
        .then((res) => {
            setInDb(res.data.pets)
        })
        .catch((err) => console.log("something went wrong in API request", err))

        
        
        axios.post("http://localhost:8000/api/pets", data)
        .then((res) => {
            console.log(res);
            navigate("/")
        })
        .catch((err) => {
            console.log("something went wrong with API request", err);
            // console.log(err.reponse.data.err.errors)
            setErrors(err.response.data.error.errors)
        })
    }

    return(
        <div>
            <div className=''>
                <div className='container-fluid'>
                    <h1 className='navbar-brand'>Pet Shelter</h1>
                    <Link to={"/"} className="">Home</Link>
                </div>
            </div>
            <div>
                {errors.name && <p className="">{errors.name.message}</p>}
                {errors.petType && <p className="">{errors.petType.message}</p>}
                {errors.description && <p className="">{errors.description.message}</p>}
                <Form  
                submitForm = {submitForm}
                initialName = ''
                initialPeType = ''
                initialDescription = ''
                />
            </div>
        </div>
    );
}

export default NewPet;
