import React, {useState, useEffect} from "react";
import axios from "axios";
import { Link } from "react-router-dom";

function AllPets () {
    const [pets, setPets] = useState([]);
    const [likey, setLikey] = useState(true)

    useEffect(() => {
        axios.get("http://localhost:8000/api/pets")
        .then((res) => {
            setPets(res.data.pets);
            console.log(res)
        })
        .catch((err) => console.log("something went wrong in API request", err))
    }, [])


    const DeletePet = (petId) =>{
        axios.delete(`http://localhost:8000/api/pets/${petId}`)
        .then((res) => {
            console.log(res);
        })
        .catch((err) => {console.log("something went wrong with API request", err)})
        setPets(pets.filter((pet) => pet._id !== petId))
    } 

    const likePet = (id, k) => {
        
        const like = document.querySelectorAll('#lik');
        for(var i=0; i<like.length; i++) {
            if(i === k){
                like[i].disabled = likey
                console.log(i)
            }
        }
    }

    return(
        <div>
            <div>
                <div className='container-fluid'>
                    <h1 className='navbar-brand'>Pet Shelter</h1>
                    <Link to={"/new"} className="btn btn-sm btn-primary">Add New Pet</Link>
                </div>
            </div>
            <div>
            <table  className="table table-hover table-bordered table-striped">
                        <thead>
                            <tr>
                                <th>Pets</th>
                                {
                                    pets.map((pet) => {
                                        <th key={pet._id}>{pet.petType}</th>
                                    })
                                    
                                
                                }
                                <th>Actions</th>
                            </tr>    
                        </thead>
                        <tbody>
                            
                    {
                        pets.map((pet, i) => (
                            <tr key={pet._id}>
                                <td>{pet.name}</td>
                        
                                <td>
                                    {/* <span> <Link to={`/viewPet/${pet._id}`} className="">View</Link></span> */}
                                    <span> <Link to={`/editPet/${pet._id}`} className="">Update Pet</Link></span>
                                    <span>  <button onClick={(e) => DeletePet(pet._id)} className="">Adopt</button> </span>
                                    <span><button onClick={(e) => likePet(pet._id, i)} className="like" id="lik">Like</button></span>
                                </td>
                            </tr>
                            ))}
                        </tbody>
                    </table>
            </div>
        </div>
    );
}

export default AllPets;
